import { all, call, put, takeLatest } from "redux-saga/effects";
import UserActionTypes from "./user.types";
import { SignInSuccess, SignInFailure } from "./user.actions";
import {
  auth,
  googleProvider,
  createUserProfileDocument
} from "../../firebase/firebase.util.js";

export function* signInWithGoogle() {
  try {
    const { user } = yield auth.signInWithPopup(googleProvider);
    const userRef = yield call(createUserProfileDocument, user);
    const userSnapshot = yield userRef.get();
    yield put(SignInSuccess({ id: userSnapshot.id, ...userSnapshot.data() }));
  } catch (error) {
    yield put(SignInFailure(error));
  }
}
export function* onGoogleSignInStart() {
  yield takeLatest(UserActionTypes.GOOGLE_SIGN_IN_START, signInWithGoogle);
}

export function* signInWithEmail(emailAndPassword) {
  try {
    const { email, password } = emailAndPassword;
    const { user } = yield auth.signInWithEmailAndPassword(email, password);
    yield put(SignInSuccess(user));
  } catch (error) {
    yield put(SignInFailure(error));
  }
}
export function* onEmailSignInStart(emailAndPassword) {
  yield takeLatest(
    UserActionTypes.EMAIL_SIGN_IN_START,
    signInWithEmail(emailAndPassword)
  );
}
export function* userSagas() {
  yield all([
    call(onGoogleSignInStart),
    call(onEmailSignInStart(emailAndPassword))
  ]);
}
